import React from 'react'
import { useNegociosData } from '../hooks/useNegociosData';
import { useState } from 'react'

export const Button = () => {
  const [categoryName, setCategoryName] = useState('Todos');

  console.log(categoryName)
  const { isLoading, data, isError, error } = useNegociosData(
    // onSuccess,
    // onError
  )
  const negocios = data.data.map(negocio => negocio)
  const [menuItem, setMenuItem] = useState(negocios);

  const filter = (button) =>{

    if(button === 'Todos'){
      setMenuItem(negocios);
      return;
    }

    const filteredData = negocios.filter(item => negocios.cat_name ===  button);
    setMenuItem(filteredData)
  }


  if (isLoading) {
    return (
      <div className="flex justify-center items-center">
      <div className="w-12 h-12 border-4 border-blue-600 rounded-full loader justify-center"></div>
      </div>
      )
  }
  if (isError) {
    return <h2>{error.message}</h2>
  }
  const allCategories =['Todos',  ...new Set(data.data.map(negocio => negocio.cat_name))]

  console.log("estos son los negocios: ",negocios)

  const hello = () => setCategoryName(this)
  return (
    <>
    {
      allCategories.map(category => {
        console.log("puede ser pa",category);
        return(
          <>
           <button onClick={() => console.log(negocios.filter(negocio => negocio.cat_name === category).map(negocio => negocio.title))} className="bg-red-600 p-4 rounded-xl">{category}</button>

           </>
        )
      })
    }
    </>
  )
}
