import React from 'react'
import { useNegociosData } from '../hooks/useNegociosData';
import { useState } from 'react'

export const Button = () => {
  const { isLoading, data, isError, error } = useNegociosData(
    // onSuccess,
    // onError
  )


  if (isLoading) {
    return (
      <div className="flex justify-center items-center">
      <div className="w-12 h-12 border-4 border-blue-600 rounded-full loader justify-center"></div>
      </div>
      )
  }
  if (isError) {
    return <h2>{error.message}</h2>
  }
  const allCategories =['Todos',  ...new Set(data.data.map(negocio => negocio.category))]
  const [categoryName, setCategoryName] = useState('Todos');
  const hello = () => console.log("we gon make it")
  return (
    <>
    {
      allCategories.map(category => {
        console.log("puede ser pa",category);
        return(
          // <button onClick={hello} className="bg-red-600 p-4 rounded-xl">{category}</button>
          <button key={index} onClick={() => setCategoryName(category)}>
          {category}
        </button>
        )
      })
    }
    </>
  )
}
