import React from 'react'
import { QueryClientProvider, QueryClient} from 'react-query';
import { useQuery } from "react-query";
import axios from 'axios';
import { Link } from 'react-router-dom';


const fetchNegocios = () => {
  return axios.get('http://127.0.0.1:8000/api/negocios')
}

export const Negocios = () => {
  const {isLoading, data, isError, error, isFetching} = useQuery('negocio', fetchNegocios)
  console.log(isLoading, isFetching);
  if (isLoading) {
    return (
      <div className="flex items-center justify-center">
      <div className="spinner-border animate-spin inline-block w-12 h-12 border-4 rounded-full" role="status">
        <span className="visually-hidden">S</span>
      </div>
      </div>
      )
  }
  if (isError) {
    return <h2>{error.message}</h2>
  }
  return (
    <div className="flex justify-center columns-4 gap-4">

      {
        data?.data.map((negocio) => {
          console.log(negocio.image)
          return (
          <div key={negocio.id} className="rounded-lg shadow-lg py-4 bg-white max-w-sm hover:bg-slate-200">
          <Link className="text-2xl" to={`/${negocio.id}`}>
            <a href="">
            <img className="rounded-t-lg" src={`http://127.0.0.1:8000${negocio.image}`} alt=""/>
            </a>
            <div className="p-6">

            <h3 className="text-gray-900 text-xl font-medium mb-2">Victor Supp Crack</h3>
            <h3 className="text-gray-900 text-xl font-medium mb-2">{negocio.title}</h3>
            <h3 className="text-gray-900 text-xl font-medium mb-2">{data?.data.description}</h3>
            <h3 className="text-gray-900 text-xl font-medium mb-2">{negocio.id}</h3>

            </div>
            </Link>
          </div>


          )
        }) 
      }
    </div>
    )
}
