import './App.css';
import { QueryClientProvider, QueryClient} from 'react-query';
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import {Negocios} from './pages/Negocios';
import {Negocio} from './pages/Negocio';
import {Contacto} from './pages/Contacto';
import  Button from './components/Button';
import  Menu  from './components/Menu';
import { useState } from 'react'
import { useNegociosData } from './hooks/useNegociosData';

const queryClient = new QueryClient()



function App() {

  const { isLoading, data, isError, error } = useNegociosData(
    // onSuccess,
    // onError
  )
  const negocios = data?.data.map((negocio) => negocio)
  const allCategories =['Todos',  ...new Set(data.data.map(negocio => negocio.cat_name))]
  console.log("This is from app page: ", negocios)
  const [menuItem, setMenuItem] = useState(negocios);
  const [buttons, setButtons] = useState(allCategories);
  if (isLoading) {
    return (
      <div className="flex justify-center items-center">
      <div className="w-12 h-12 border-4 border-blue-600 rounded-full loader justify-center"></div>
      </div>
      )
  }
  if (isError) {
    return <h2>{error.message}</h2>
  }


  //Filter Function
  const filter = (button) =>{

    if(button === 'Todos'){
      setMenuItem(negocios);
      return;
    }

    const filteredData = negocios.filter(negocio => negocio.category ===  button);
    setMenuItem(filteredData)
  }


  return (
    <QueryClientProvider client={queryClient}>
      <Router>
      <nav className="bg-white py-8 mb-8 text-right">
        <Link className="text-2xl px-2" to="/"> Inicio </Link>
        <Link className="text-2xl px-2" to="/contacto"> Contacto </Link>


      </nav>


      <Routes>
        <Route path="/" element={<Negocios />} />
        <Route path="/:negocioId" element={<Negocio/>}/>
        <Route path="/contacto" element={<Contacto />} />

      </Routes>
        <div className="App">

        <div className="title">
          <h1>
            Portfolio 
            <span> Filter</span>
          </h1>
        </div>


        <Button button={buttons} filter={filter} />
        <Menu menuItem={menuItem}/>

      </div>
      <footer className="mt-80 h-10 bg-blue-500 h-screen">Footer</footer>
    </Router>

      </QueryClientProvider>
  );
}

export default App;
