import React from 'react'
import { QueryClientProvider, QueryClient} from 'react-query';
import { useQuery } from "react-query";
import axios from 'axios';
import { useNegociosData } from '../hooks/useNegociosData';
import { Link } from 'react-router-dom';



export const Negocios = () => {

  const onSuccess = data => {
    console.log({ data })
  }

  const onError = error => {
    console.log({ error })
  }

  const { isLoading, data, isError, error } = useNegociosData(
    onSuccess,
    onError
  )

  if (isLoading) {
    return (
      <div className="flex justify-center items-center">
      <div className="w-12 h-12 border-4 border-blue-600 rounded-full loader justify-center"></div>
      </div>
      )
  }
  if (isError) {
    return <h2>{error.message}</h2>
  }

return (
  <div className="flex justify-center columns-4 gap-4">
    <h3 className="text-gray-900 text-xl font-medium mb-2">Productos y Servicios</h3>
    {
      data?.data.map((negocio) => {
        console.log(negocio.image)
        return (
        <div key={negocio.id} className="rounded-lg shadow-lg py-4 bg-white max-w-sm hover:bg-slate-200">
        <Link className="text-2xl" to={`/${negocio.id}`}>

          <img className="rounded-t-lg object-cover max-h-64" src={`http://127.0.0.1:8000${negocio.image}`} alt=""/>

          <div className="p-6">


          <h3 className="text-gray-900 text-xl font-medium mb-2">{negocio.title}</h3>
          <h3 className="text-gray-900 text-xl font-medium mb-2">{data?.data.description}</h3>
          <h3 className="text-gray-900 text-xl font-medium mb-2">{negocio.cat_name}</h3>

          </div>
          </Link>
        </div>


        )
      }) 
    }
  </div>
  )
}
